Resources.SetScript:
$users = (net localgroup administrators)
if ($users -notcontains $acctname)
{
	Write-Verbose "Adding [$acctname] to local administators group."
	net localgroup administrators $acctname /Add | Out-Null
}

$nodeName = $env:COMPUTERNAME
If($nodeName.IndexOf($SQLNodeNamePrefix, [System.StringComparison]::OrdinalIgnoreCase) -gt -1)
{
    $LoadBalancerProbeRuleName = "SQL AG Listener LB Probe (TCP-In)($LoadBalancerProbePort)"
    Import-Module NetSecurity -DisableNameChecking
    if (!(Get-NetFirewallRule -DisplayName $LoadBalancerProbeRuleName -ErrorAction Ignore))
    {
	    Write-Verbose "Adding firewall rule '$LoadBalancerProbeRuleName'"
	    New-NetFirewallRule -DisplayName $LoadBalancerProbeRuleName -Enabled True `
	        -Action Allow -Direction Inbound -LocalPort $LoadBalancerProbePort -Protocol TCP -IcmpType Any | Out-Null
    }
}

[System.Reflection.Assembly]::LoadWithPartialName('Microsoft.SqlServer.SMO')
$s = new-object ('Microsoft.SqlServer.Management.Smo.Server') $env:COMPUTERNAME
$s.Settings.LoginMode = [Microsoft.SqlServer.Management.SMO.ServerLoginMode]::Mixed
$s.Alter()
$list = Get-Service -Name MSSQL*
foreach ($s in $list)
{
   Set-Service -Name $s.Name -StartupType Automatic
   if ($s.Status -ne "Stopped")
   {
       $s.Stop()
       $s.WaitForStatus("Stopped")
       $s.Refresh()
   }
   if ($s.Status -ne "Running")
   {
      $s.Start()
      $s.WaitForStatus("Running")
      $s.Refresh()
   }
}
Invoke-SqlCmd -Query "ALTER LOGIN sa ENABLE" -ServerInstance "."
Invoke-SqlCmd -Query "ALTER LOGIN sa WITH PASSWORD = '$SqlAdminPassword'" -ServerInstance "."

$users = (net localgroup administrators)
if ($users -notcontains $acctname)
{
	Write-Verbose "Adding [$acctname] to local administators group."
	net localgroup administrators $acctname /Add | Out-Null
}

Resources.TestScript:
If(Test-Path "$env:ProgramFiles\Microsoft SQL Server\Client SDK\ODBC\110\Tools\Binn\SqlCmd.exe")
{
    return $true
}
return $false
